Firestore firestore = Firestore.instance;
DocumentReference onProgressDocumentReference,
    caseDocumentReference,
    cardDocumentReference;

toothOnTap(
    BuildContext context,
    List cases,
    List tooth,
    List<String> toothPosition,
    String _case,
    String position,
    int toothNumber) {
  onProgressDocumentReference = firestore
      .collection('FunD')
      .document('funD')
      .collection('Clinic')
      .document('clinic')
      .collection(clinic)
      .document(clinic)
      .collection('Patients')
      .document(uid)
      .collection('History')
      .document('history')
      .collection('OnProgress')
      .document('onProgress');

  caseDocumentReference = firestore
      .collection('FunD')
      .document('funD')
      .collection('Clinic')
      .document('clinic')
      .collection(clinic)
      .document(clinic)
      .collection('Patients')
      .document(uid)
      .collection('DentalCase')
      .document('dentalCase');

  cardDocumentReference = firestore
      .collection('FunD')
      .document('funD')
      .collection('Clinic')
      .document('clinic')
      .collection(clinic)
      .document(clinic)
      .collection('Patients')
      .document(uid)
      .collection('DentalCase')
      .document('dentalCase')
      .collection('CaseDetail')
      .document('caseDetail');
  dentalHistory();
  DateTime _currentDate = new DateTime.now();
  String timeDate =
      _currentDate.toString().substring(0, _currentDate.toString().length - 7);
  for (var i = 0; i < cases.length; i++) {
    if (cases[i]['Case'] == '$_case') {
      cases.removeAt(i);
    }
  }
  for (var i = 0; i < tooth.length; i++) {
    if (tooth[i]['Case'] == '$_case' && tooth[i]['Position'] == '$position') {
      tooth.removeAt(i);
    }
  }
  for (var i = 0; i < toothPosition.length; i++) {
    if (toothPosition[i] == '$_case') {
      toothPosition.removeAt(i);
    }
  }
  for (var i = 0; i < onProgressHistory.length; i++) {
    if (onProgressHistory[i]['Detail'] ==
        'Tooth $toothNumber $position : $_case') {
      onProgressHistory.removeAt(i);
    }
  }
  toothPosition.add('$_case');
  cases.add(addCase = {
    'Case': '$_case',
    'Date': timeDate,
    'Dentist': dentistName,
    'Position': '$position'
  });
  tooth.add(addCase = {
    'Case': '$_case',
    'Date': timeDate,
    'Dentist': dentistName,
    'Position': '$position',
    'Detail': 'Tooth $toothNumber $position : $_case'
  });
  onProgressHistory.add(map = {
    'Date': timeDate,
    'Dentist': dentistName,
    'Position': '$position',
    'Status': 'On Progress',
    'Detail': 'Tooth $toothNumber $position : $_case'
  });
  caseDocumentReference.updateData({'tooth $toothNumber $position': cases});
  cardDocumentReference.updateData({'tooth $toothNumber': tooth});
  onProgressDocumentReference.updateData({'history': onProgressHistory});
  Navigator.pop(context);
}

